package com.zhy.drift.cache.bean;

import com.zhy.drift.util.Time;

public class BottleCache extends AbstractCache{

	private Long bottleId;

	public BottleCache(Long bottleId){
		super(Time.DAY);
		this.bottleId = bottleId;
	}
	
	public Long getBottleId() {
		return bottleId;
	}

	public void setBottleId(Long bottleId) {
		this.bottleId = bottleId;
	}
}
